#!/bin/sh
sleep 60s;if ! ./vendor/bin/drush status | grep -q "Drupal bootstrap"; then echo "We are going to install the site named ${DRUPAL_SITE_NAME}"; ./vendor/bin/drush site-install standard --account-name=${DRUPAL_USER} --account-pass=${DRUPAL_PASSWORD}  --db-url="${DB_DRIVER}://${DB_USER}:${DB_PASSWORD}@${DB_HOST}/${DB_NAME}" --site-name=${DRUPAL_SITE_NAME} --site-mail=${DRUPAL_SITE_MAIL} --locale=${DRUPAL_LOCALE} -y; echo "We are done creating the site named ${DRUPAL_SITE_NAME}, with this settings: \n DB_DRIVER: ${DB_DRIVER} \n DB_USER: ${DB_USER} \n DB_PASSWORD: ${DB_PASSWORD} \n DB_HOST: ${DB_HOST} \n DB_NAME: ${DB_NAME} \n DRUPAL_USER: ${DRUPAL_USER} \n DRUPAL_PASSWORD: ${DRUPAL_PASSWORD} \n DRUPAL_SITE_NAME: ${DRUPAL_SITE_NAME} \n DRUPAL_SITE_MAIL: ${DRUPAL_SITE_MAIL} \n DRUPAL_LOCALE: ${DRUPAL_LOCALE}"; else echo "The site named ${DRUPAL_SITE_NAME} is already installed"; fi;


